import React, { Component } from "react";

class ClassClick extends Component {
  clickHander() {
    console.log("Click the button");
  }
  render() {
    return (
      <div>
        <button onClick={this.clickHander}>Click Me</button>
      </div>
    );
  }
}

export default ClassClick;
