import React from "react";

function NameList() {
  //   const names = ["Bruce", "Clark", "Diana", "Bolu"];
  //   const nameDisplay = names.map((name) => <h2>{name}</h2>);
  function NameList() {
    const persons = [
      {
        id: 1,
        name: "Daniel",
        age: 29,
        skill: "Angular",
      },
      {
        id: 2,
        name: "Albert",
        age: 32,
        skill: "Vue",
      },
      {
        id: 3,
        name: "Samuel",
        age: 19,
        skill: "React",
      },
    ];

    const nameAgeSkill = persons.map(
      (nas) =>
        `my name is ${nas.name}, I am ${nas.age} years old and I code ${nas.skill}`
    );
  }

  return (
    <div>
      {/* {nameDisplay} */}
      {nameAgeSkill}
    </div>
  );
}

export default NameList;
